const e=JSON.parse('{"key":"v-4df9b36a","path":"/zh/response-data-management/custom-set-cache-data.html","title":"手动更新缓存","lang":"zh-CN","frontmatter":{"title":"手动更新缓存","order":40,"summary":"有些服务接口支持批量请求数据，它意味着总是由不确定的若干组响应数据组成，当我们想要在初始化页面时批量请求数据，然后在交互中只请求单条数据的情况下，会造成缓存穿透的问题。 例如我们需要按日期获取todo列表数据，在初始化时一次请求获取了5月1日到5日，5天的数据，然后用户在操作时又获取了一次5月1日的数据，此时不会命中初始化时的5月1日数据，因为初始化的5天数","head":[["meta",{"property":"og:url","content":"https://alova.js.org/zh/response-data-management/custom-set-cache-data.html"}],["meta",{"property":"og:site_name","content":"Alova.js"}],["meta",{"property":"og:title","content":"手动更新缓存"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:updated_time","content":"2022-11-11T10:28:05.000Z"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:locale:alternate","content":"en-US"}],["meta",{"property":"article:modified_time","content":"2022-11-11T10:28:05.000Z"}],["link",{"rel":"alternate","hreflang":"en-us","href":"https://alova.js.org/response-data-management/custom-set-cache-data.html"}]]},"excerpt":"","headers":[{"level":2,"title":"更新静态缓存数据","slug":"更新静态缓存数据","link":"#更新静态缓存数据","children":[]},{"level":2,"title":"动态缓存数据更新","slug":"动态缓存数据更新","link":"#动态缓存数据更新","children":[]},{"level":2,"title":"中断缓存更新","slug":"中断缓存更新","link":"#中断缓存更新","children":[]}],"git":{"createdTime":1666882918000,"updatedTime":1668162485000,"contributors":[{"name":"JOU Amjs","email":"huzhen555@qq.com","commits":2},{"name":"hu","email":"huzhen555@qq.com","commits":1}]},"readingTime":{"minutes":3.88,"words":1164},"filePathRelative":"zh/response-data-management/custom-set-cache-data.md","localizedDate":"2022年10月27日"}');export{e as data};
